mapper.select:
SELECT * FROM tb_member
WHERE
state != 2
AND
(username LIKE #{search}
OR
phone LIKE #{search}
OR
email LIKE #{search}
OR
sex LIKE #{search}
OR
address LIKE #{search})

  AND
  created BETWEEN #{minDate} AND #{maxDate}

ORDER BY
${orderCol}
${orderDir}

